import styled from 'styled-components';
import {Link} from 'react-router-dom';

import { CartIcon } from '../cart-icon/cart-icon.component';
import { WishListIcon } from '../wishlist-icon/wishlist-icon.component';
import { SearchIcon } from '../search-icon/search-icon.component';
import { MobileMenu} from '../mobile-menu/mobile-menu.component';
import UserUnlock from '../user-unlocked/user-unlocked.component';
import UserLock from '../user-lock/user.lock.component';

export const HeaderContainer = styled.div`
  height: 70px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  margin-bottom: 25px;

  @media screen and (max-width: 800px) {
    height: 60px;
    padding: 10px;
    margin-botttom: 20px;
  }
`;

export const LogoContainer = styled(Link)`
  height: 100%;
  width: 70px;
  padding: 25px;
  align-items: center;
  @media screen and (max-width: 800px) {
    width: 50px;
    padding: 0
  }
`;

export const OptionsContainer = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-start;

  @media screen and (max-width: 800px) {
    width: 80%;
    display: none;

  }
`;

export const IconsContainer = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;



export const OptionLink = styled(Link)`
  padding: 10px 15px;
  cursor: pointer;
`;
