import React from 'react';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';

import CollectionPage from './collection.component';
import Spinner from '../../components/with-spinner/with-spinner.component';

const GET_COLLECTION_BY_TITLE = gql`
  query getCollectionsByTitle($title: String!){
    getCollectionByTitle(title: $title) {
      id
      title
      items {
        id 
        name 
        price
        imageUrl
      }
    }
  }
`;

const CollectionPageContainer = ({match}) => (
  <Query query={GET_COLLECTION_BY_TITLE} variables={{ title: match.params.colllectionId }}>
    {
      ({loading, data: {getCollectionByTitle}}) => {
        if (loading) return <Spinner />
        return <CollectionPage collection={getCollectionByTitle}/>;
      }
    }
  </Query>
);

export default CollectionPageContainer;
